import React, { Component } from 'react';
import Shopping from '../Shopping';
import './List.scss';

class List extends Component {
	constructor(props) {
		super(props);
		this.filter = this.filtro.bind(this);
		this.state = {};
	}

	filtro(data, filterTag) {
		let filterData = data.filter((obj) => {
			let arr = obj.arrTags;
			for(let i = 0; i < arr.length; i++) {
				console.log(arr[i]);
				if(arr[i] == filterTag.filterTag) {
					return true;
				}
			}
			return false;
		})
		console.log(filterData);
	}

	render() {
		const { filterTag } = this.props;
		const { data } = this.props;
		let showOnScreen;
		if(filterTag === '') {
			showOnScreen = data.map((shopping, index) => (
				<Shopping 
					key={index}
					urlImage={shopping.urlImage}
					text={shopping.text}
					arrTags={shopping.arrTags}
				/>
			))
		} else {
			let dados = data.filter(filtro(data, filterTag));
			showOnScreen = this.filter(data, filterTag);
		}
		return (
			<div id="list">{showOnScreen}</div>
		);
	}
}

export default List;
