import React, { Component } from 'react';
import Shopping from '../Shopping';
import './List.scss';

class List extends Component {
	constructor(props) {
		super(props);
		this.filter = this.filtro.bind(this);
		this.state = {};
	}

	filtro(data, filterTag) {
		for(let i = 0; i < data.length; i++) {
			let obj = data[i];
			let arr = obj.arrTags;
			for(let j = 0; j < arr.length; j++) {
				if(arr[j] === filterTag.filterTag) {
					console.log('true');
				}
			}
		}
		console.log('false');
	}

	render() {
		const { filterTag } = this.props;
		const { data } = this.props;
		let showOnScreen;
		if(filterTag === '') {
			showOnScreen = data.map((shopping, index) => (
				<Shopping 
					key={index}
					urlImage={shopping.urlImage}
					text={shopping.text}
					arrTags={shopping.arrTags}
				/>
			))
		} else {
			this.filtro(data, filterTag);
			showOnScreen = this.filter(data, filterTag);
		}
		return (
			<div id="list">{showOnScreen}</div>
		);
	}
}

export default List;
