import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import { 
	BrowserRouter as Browser,
	Switch,
	Route,
} from 'react-router-dom';
import { ShoppingList, Car } from './views'
import './global/refs.scss';

class App extends Component {
	constructor(props) {
		super(props);
		this.handleAdd = this.handleAdd.bind(this);
		this.handleDelete = this.handleDelete.bind(this);
		this.state = {
			prodAdds: [],
		};
	}

	handleAdd(add) {
		const { prodAdds } = this.state;
		this.setState({
			prodAdds: [add, ...prodAdds],
		});
	}

	handleDelete(deleteName) {
		const { prodAdds } = this.props;
		console.log(prodAdds);
		console.log(typeof name);
		console.log(typeof deleteName);
		this.setState({
			prodAdds: prodAdds.filter(({ name }) => (name !== deleteName)),
		});
	}

	render () {
		const { prodAdds } = this.state;
		let lengthCar = prodAdds.length;
		return (
			<Browser>
				<div id="app">
					<Switch>
						<Route exact path="/">
							<ShoppingList add={this.handleAdd} length={lengthCar} />
						</Route>
						<Route path="/carrinho">
							<Car prodAdds={prodAdds} length={lengthCar} handleDelete={this.handleDelete} />
						</Route>
					</Switch>
				</div>
			</Browser>
		);
	}
}

ReactDOM.render(<App />, document.getElementById('root'));
