import React from 'react';
import PropTypes from 'prop-types';
import './Shopping.scss';

function Shopping({name, urlImage, text, arrTags, id}) {
	const handleAdd = (name, description, id) => {
		console.log(event.target.id);
	}

	return (
		<div id="container">
			<div>
				<img src={urlImage} alt={name} />
			</div>
			<div><p>{text}</p></div>
			<div className="assets">
				{/*Itera pelo array de tags que Ã© passado e coloca cada uma em uma tag <span>*/}
				<div className="tags" id={id}>
					{arrTags.map((tag, k) => (
						<span key={k}>{tag}</span>
					))}
				</div>
				<button type="button" onClick={handleAdd(name, text, id)}>Adicionar ao Carrinho</button>
			</div>
		</div>
	);
}

Shopping.propTypes = {
	name: PropTypes.string,
	urlImage: PropTypes.string,
	text: PropTypes.string,
	arrTags: PropTypes.arrayOf(PropTypes.string),
	id: PropTypes.number,
};

Shopping.defaultProps = {
	name: 'product name',
	urlImage: './#',
	text: 'Put some text inside here!',
	arrTags: ['tag', 'tag', 'tag'],
	id: new Date().getTime(),
};

export default Shopping;
